using System.ComponentModel.Composition;
using XrmToolBox.Extensibility;
using XrmToolBox.Extensibility.Interfaces;

namespace Cobalt.XrmToolbox.EntityUpdater
{
    [Export(typeof(IXrmToolBoxPlugin)),
       ExportMetadata("Name", "Bulk Update Entity Change Tracking"),
       ExportMetadata("Description", "This plugin allows for the user to update the change tracking property on entities in bulk."),
       // Please specify the base64 content of a 32x32 pixels image  
       ExportMetadata("SmallImageBase64", ""),
       // Please specify the base64 content of a 80x80 pixels image  
       ExportMetadata("BigImageBase64", ""),
       ExportMetadata("BackgroundColor", "White"),
       ExportMetadata("PrimaryFontColor", "Black"),
       ExportMetadata("SecondaryFontColor", "Gray")]
    public class EntityUpdaterPlugin : PluginBase
    {
        public override IXrmToolBoxPluginControl GetControl()
        {
            return new EntityUpdaterControl();
        }
    }
}